["custom"] = "-- Shift+鼠标左右键点击设置焦点\nlocal modifier = \"shift\" ---- 可修改为 shift, alt 或者 ctrl\nlocal mouseButton = \"1\" ---- 1 =鼠标左键, 2 = 鼠标右键, 3 = 鼠标滚轮按下, 4 and 5 = 高级鼠标……\n\nlocal function SetFocusHotkey(frame)\n    frame:SetAttribute(modifier..\"-type\"..mouseButton,\"focus\")\nend\n\nlocal function CreateFrame_Hook(type, name, parent, template)\n    if template == \"SecureUnitButtonTemplate\" then\n        SetFocusHotkey(_G[name])\n    end\nend\n\nhooksecurefunc(\"CreateFrame\", CreateFrame_Hook)\n\n---- Keybinding override so that models can be shift/alt/ctrl+clicked\nlocal f = CreateFrame(\"CheckButton\", \"FocuserButton\", UIParent, \"SecureActionButtonTemplate\")\nf:SetAttribute(\"type\",\"macro\")\nf:SetAttribute(\"macrotext\",\"/focus mouseover\")\nf:RegisterForClicks(\"AnyDown\", \"AnyUp\")\nSetOverrideBindingClick(FocuserButton,true,modifier..\"-BUTTON\"..mouseButton,\"FocuserButton\")\n\n---- Set the keybindings on the default unit frames since we won't get any CreateFrame notification about them\nlocal duf = {\n    PlayerFrame,\n    PetFrame,\n    TargetFrame,\n    FocusFrame,\n    TargetFrameToT,\n    FocusFrameToT,\n    Boss1TargetFrame,\n    Boss2TargetFrame,\n    Boss3TargetFrame,\n    Boss4TargetFrame,\n    Boss5TargetFrame,\n    ArenaEnemyMatchFrame1,\n    ArenaEnemyMatchFrame2,\n    ArenaEnemyMatchFrame3,\n    ArenaEnemyMatchFrame4,\n    ArenaEnemyMatchFrame5,\n}\n\nfor i,frame in pairs(duf) do\n    SetFocusHotkey(frame)\nend\n\nfor _, p in pairs({ PartyFrame:GetChildren() }) do\n    SetFocusHotkey(p)\nend\n\n\n\n",
},
