["custom"] = "function(event, ...)\n    if event == \"PLAYER_REGEN_ENABLED\" then \n        aura_env.ResetMarks()\n        \n        aura_env.Queue = {} -- Reset Queue\n        \n        aura_env.UpdatePlayerMarks()\n    elseif event == \"WA_DELAYED_PLAYER_ENTERING_WORLD\" then\n        aura_env.UpdatePlayerInfo()\n        \n        aura_env.SendPlayerInfo()\n        \n        WeakAuras.ScanEvents(\"AutomarkGetMobsRequest\")\n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local _, _, _, _, _, _, _, guid = ...\n        -- Get the NPC ID associated with the GUID\n        local npcId = aura_env.GetNpcIdFromGUID(guid)\n        \n        -- Get the mob data for the NPC\n        local mob = aura_env.Mobs[npcId]\n        \n        \n        -- Check if the dead unit was in our table; if not, return\n        if not mob then\n            return\n        end\n        \n        -- Remove the dead unit from the queue if it was present\n        aura_env.Queue[guid] = nil\n        \n        -- Check if the dead unit had an associated mark and deactivate it\n        for _, mark in pairs(aura_env.Marks) do\n            if mark.Guid == guid then\n                mark.Active = false\n                break\n            end\n        end\n        \n        -- Check if the next mob in the queue should be marked\n        local markNextInQueue = mob.UnitDied == 2\n        \n        if markNextInQueue then\n            aura_env.MarkNextInQueue()\n        end\n    end\nend",
